import {APIResponse} from "./models/api.model";
import {UserDTO} from "./models/user.dto";
import {AddUserDTO} from "./models/add-user.dto";
import { get, post } from "./utils.api";
import axios from 'axios';

export const add = async (name: string, surname: string): Promise<APIResponse<{ token: string }>> => {
    const res = await post(`/users`, { name, surname });
    return await res.json();
};

export class UserApi {
    public static async getAll(): Promise<UserDTO[]> {
        const resp = await fetch('http://localhost:3000/users', {
            method: 'GET'
        })

        return await resp.json();
    }


    public static async add(data: AddUserDTO) {
        const resp = await axios({
            url: 'http://localhost:3000/users',
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            data: JSON.stringify(data),
        }).then(function (res) {
            console.log(res.data);
        });
    }

    public static async add1(data: AddUserDTO) {
        const resp = await fetch('http://localhost:3000/users', {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
                "Access-Control-Allow-Origin" : "*",
                "Access-Control-Allow-Credentials" : 'true'
            },
            body: JSON.stringify(data),
        });

        return resp.json();

    };

}
