import {UserDTO} from "./dto/user.dto";
import {AddUserDTO} from "./dto/add-user.dto";
import axios from 'axios';

export class UserApi {
    public static async getAll(): Promise<UserDTO[]> {
        const resp = await fetch('http://localhost:3000/users', {
            method: 'GET'
        })

        return await resp.json();
    }

    public static async add(addRequest: AddUserDTO) {
        const resp = await fetch('http://localhost:3000/users', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            //body: JSON.stringify(user: addRequest)
        })

        return await resp.json();
    }

    public static async add1(data: AddUserDTO) {
        const response = await axios.post('http://localhost:3000/users', {user: data});

        axios
            .post('http://localhost:3000/users', {
                name: data.name,
                surname: data.surname,
            })
            .then((response) => {
                setPost(response.data);
            });
    }

}
